        [Test]
        public void CreateVolumeAnnotations()
        {
            ServiceClient.Post(new VolumeAnnotationCreate { BookId = "bible", VolumeNumber = 1, AnnotationNumber = 1, Title = "书名", Annotation = "《创世记》(Genesis)这个书名来自希腊文的旧约圣经(七十士译本)，意思是「起源」、「源头」、「创造」等，而希伯来文圣经的书名(根据这卷书起首的字)则是《起初》。这两个书名都恰当地表达了本书的内容，因为本书是描述一切事物的来源，例如宇宙、世界、人类、人类的制度(如婚姻)、国家和至为重要的以色列人。《创世记》集中记载神如何借着创造，使这一切都出现。另外一个比较少用的书名是《摩西的第一卷书》。这书名强调《创世记》是摩西五经的第一卷。传统认为摩西是这些书卷的作者，所以它们又称为「律法书」。《创世记》为西乃山的颁布律法(《出埃及记》至《申命记》的内容)提供了一个历史背景，又为解释这些书卷的律法和故事提供了神学上的钥匙。" }).PrintDump();
            ServiceClient.Post(new VolumeAnnotationCreate { BookId = "bible", VolumeNumber = 1, AnnotationNumber = 2, Title = "作者", Annotation = "圣经开头的五卷书，据信都是摩西写的，故称为「摩西五经」。本书是「摩西五经」中的第一卷。" }).PrintDump();
            ServiceClient.Post(new VolumeAnnotationCreate { BookId = "bible", VolumeNumber = 1, AnnotationNumber = 3, Title = "写作时地", Annotation = "摩西是大约主前1500~1400年的人物，而本书内容所描述的时间，大约自主前4000年至主前1700年，前后共约二千三百年。摩西如何能知道在他多年以前的历史呢？当是根据于他在西乃山上和旷野间从神那里所得的启示和晓谕(参徒七37~38)。" }).PrintDump();
            ServiceClient.Post(new VolumeAnnotationCreate { BookId = "bible", VolumeNumber = 1, AnnotationNumber = 4, Title = "主旨要义", Annotation = "本书给我们看见神的创造和人的堕落。神创造宇宙万有，并且造了人，祂的目的是要人代表祂在地上掌权，彰显祂的荣耀。但是人却堕落了，无论在何种时期和何种情况之下，都是失败的。然而人的堕落失败，并没有使神放弃祂原定的目的和计划。在人各阶段的堕落失败中，神都伸进手来，施予救援。人的堕落失败，倒给神机会来彰显祂的恩典。本书处处显示，那要来的基督是堕落之人的指望和拯救。" }).PrintDump();
            ServiceClient.Post(new VolumeAnnotationCreate { BookId = "bible", VolumeNumber = 1, AnnotationNumber = 5, Title = "写本书的动机", Annotation = "本书写作的目的是在描述人类的起源，神选民的来历，以及他们如何从迦南地迁徙至埃及地。" }).PrintDump();
            ServiceClient.Post(new VolumeAnnotationCreate { BookId = "bible", VolumeNumber = 1, AnnotationNumber = 6, Title = "本书的重要性", Annotation = "(一)创世记是全部圣经的第一卷，也是圣经中最重要的一卷。它说到：关乎神自己的启示，宇宙和万物的来历，神和人的关系，神拯救的计划，基督的预表，神带领和对付古圣的经过，等等。可以说，圣经中每种主要的事实、真理、教训和启示，都胚胎在这卷书里，正像雏鸡尚未成形时早已包含在鸡卵里了。(二)在圣经里有两卷书是魔鬼所特别忌恨的，就是创世记和启示录。两千年来，牠一直千方百计的企图破坏这两本书的信用，并推翻它们的记载，若不是说它们是「神话」，就是说它们是「迷信」。牠所以如此，是因为创世记是说到神对牠的宣判(创三14~15)，启示录是说到神对牠的处决(启廿2~3，10)。" }).PrintDump();
            ServiceClient.Post(new VolumeAnnotationCreate { BookId = "bible", VolumeNumber = 1, AnnotationNumber = 7, Title = "本书的特点", Annotation = "(一)在新约圣经中，圣灵借着主的仆人引述本书的话有六十多次；我们的主也有好几次引用它的话(参太十九4~6；廿四37~39；路十一51；约八44，56等)。(二)圣经中一切真理的种子，都先种在这本书里，以后在后面诸书中发芽、生长并结实。(三)本书的书名《创世记》(Genesis)，是希腊文《七十士译本》所给的称呼。希伯来文旧约圣经原无书名，犹太人惯于用各书开头的第一个字来命名。本书第一章第一节的头一个字是「起初」，故犹太人称呼本书为「起初」。本书就是论到天地的起初、万物的起初、人的起初、罪恶的起初、救赎的起初、宗教的起初、凶杀的起初、文化的起初、家谱的起初、世人被灭的起初、国家的起初、巴比伦的起初、神呼召的起初、信心和应许的起初、以色列人的起初、选民下埃及的起初。(四)神在本书中借着各种不同的名字，如：「神」(Elohim，一1)、「耶和华」(Jehovah，二4)、「主」(Adonai，十八27)、「耶和华以勒」(廿二14)、「至高的神」(十四20)、「全能的神」(十七1)、「永生神」(廿一33)等，把祂自己向人多方启示出来，使我们可以由此得着许多宝贵的亮光。(五)本书中虽然没有一次直接题到我们的主，可是它里面充满了关乎基督的豫表，例如：亚当的沉睡(二21~22)豫表基督十字架的死；皮子(三21)、羊群中头生的(四4)、公羊(廿二13)等，都豫表主为我们作代替的牺牲；方舟(六14)豫表基督是人惟一的拯救；以撒豫表基督顺服以至于死(廿二1~10)；麦基洗德豫表基督是另一等次的祭司(十四17~20)；约瑟(卅七至五十章)豫表主为父所爱，为弟兄所恨、所弃、所卖，后升为至高，成为弟兄的祝福和拯救，娶外邦新妇，等等。" }).PrintDump();
            ServiceClient.Post(new VolumeAnnotationCreate { BookId = "bible", VolumeNumber = 1, AnnotationNumber = 8, Title = "本书与其他圣经书卷的关系", Annotation = "一、本书是论到败坏，而《出埃及记》则论到救赎。本书结束于「收殓在棺材里，停在埃及」(创五十26)，显明人败坏的结果，是何等的黑暗与无望；《出埃及记》则不但记载神如何带领以色列人出埃及，并且最后的结局是「耶和华的荣光就充满了帐幕」(出四十34)，说明救赎的结果，带进何等的荣耀与盼望。再者，本书重在描绘神子民个人的属灵经历，而《出埃及记》则重在描绘神子民团体的属灵经历。二、在圣经里有两卷书是魔鬼所特别忌恨的，就是本书和《启示录》。历年来，牠千方百计的企图破坏它们的信用，和推翻它们的记载，若不是说它们是「神话」，就是说它们是「迷信」。牠所以如此，因为本书是说到神对牠的宣判(创三14~15)，《启示录》是说到神对牠的处决(启二十2~3，10)。" }).PrintDump();
            ServiceClient.Post(new VolumeAnnotationCreate { BookId = "bible", VolumeNumber = 1, AnnotationNumber = 9, Title = "钥节", Annotation = "「神说，我们要照着我们的形像，按着我们的样式造人，使他们管理海里的鱼，空中的鸟，地上的牲畜，和全地，并地上所爬的一切昆虫。」(一26)「园子当中又有生命树和分别善恶的树...耶和华神吩咐他说，园中各样树上的果子，你可以随意吃；只是分别善恶树上的果子，你不可吃，因为你吃的日子必定死。」(二9b，16~17)「耶和华对亚伯兰说，你要离开本地、本族、父家，往我所要指示你的地去。」(十二1)「亚伯兰信耶和华，耶和华就以此为他的义。」(十五6)" }).PrintDump();
            ServiceClient.Post(new VolumeAnnotationCreate { BookId = "bible", VolumeNumber = 1, AnnotationNumber = 10, Title = "钥字", Annotation = "「起初」(一1)「败坏」(六11)" }).PrintDump();
            ServiceClient.Post(new VolumeAnnotationCreate { BookId = "bible", VolumeNumber = 1, AnnotationNumber = 11, Title = "内容大纲", Annotation = "【被造与选召】一、上古的历史(一至十一章)1.天地万物的来历(一1~二3)2.人类的起头与堕落(二4~三24)3.人类在罪恶与死亡之下(四1~六8)4.世界受审判(六9~八14)5.立约与咒诅(八15~九28)6.分散与拣选(十一1~32)二、选民列祖的历史(十二至五十章)1.亚伯拉罕的蒙召与应许(十二~二十章)2.以撒与信心的更大考验(廿一~廿六章)3.雅各与以色列的雏形(廿七~卅六章)4.约瑟与迁入埃及(卅七~五十章)" }).PrintDump();
